mapper.update:
UPDATE cart
SET
pcolor = #{pcolor},
pamount = #{pamount},
psize = #{psize}
WHERE cartno = #{cartno}

mapper.select:
SELECT cartno
FROM cart
WHERE mno = #{mno}
AND pcode = #{pcode}
AND pcolor = #{pcolor}
AND psize = #{psize}
UNION ALL
SELECT -1 as cartno
FROM dual
WHERE NOT EXISTS( SELECT cartno
                  FROM cart
                  WHERE mno = #{mno}
                  AND pcode = #{pcode}
                  AND pcolor = #{pcolor}
                  AND psize = #{psize} )

SELECT *
FROM cart
WHERE mno = #{mno}
AND pcode = #{pcode}
AND pcolor = #{pcolor}
