mapper.select:
SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					SELECT * 
					FROM QNA 						
					) A
			)
WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
		update qna 
           set qcategory = #{qcategory}
             , qtitle = #{qtitle}
             , qcontent = #{qcontent}

         where qid = #{qid}
